mapper.select:
SELECT NVL(MAX(SORT_ORDR) + 1, 1) AS cnt FROM COMMON_CODE M WHERE 1 = 1
AND PARENTS_CODE_SEQ = #{parentsCodeSeq}
AND PARENTS_CODE_SEQ = 0

mapper.insert:
INSERT INTO COMMON_CODE
(
	CODE_SEQ
	, PARENTS_CODE_SEQ
	, CODE
	, CODE_NM
	, CODE_DC
	, USE_AT
	, CREATE_DT
	, CREATE_SEQ
	, SORT_ORDR
)
VALUES
(
	(SELECT seqNextval('CODE') FROM DUAL)
	
		
		, #{parentsCodeSeq}
		
		
		, 0
		
	
	, #{code}
	, #{codeNm}
	, #{codeDc}
	, #{useAt}
	, SYSDATE()
	, #{createSeq}
	, (SELECT IFNULL(MAX(SORT_ORDR), 0)+1 FROM COMMON_CODE AS C
		WHERE
		
			
				PARENTS_CODE_SEQ = #{parentsCodeSeq}
			
			
				PARENTS_CODE_SEQ = 0
			
		
	)
)

mapper.update:
UPDATE COMMON_CODE
SET
	CODE = #{code}
	, CODE_NM = #{codeNm}
	, CODE_DC = #{codeDc}
	
		
		, PARENTS_CODE_SEQ = #{parentsCodeSeq}
		
		
		, PARENTS_CODE_SEQ = 0
		
	
	, USE_AT = #{useAt}
	, SORT_ORDR = #{sortOrdr}
WHERE 
	CODE_SEQ = #{codeSeq}

UPDATE COMMON_CODE
SET
	SORT_ORDR = #{sortOrdr}
WHERE CODE_SEQ = #{codeSeq}
